#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Sun Feb 16 20:04:40 2020@author: alexandreduval"""# Import libraries import json # Define paths LOC = '../../data/metadata/'old_filename ='metadata.json'new_filename = 'clean_data.json'# Import json dataset try:	data = json.load(open(LOC + old_filename, 'r'))except UnicodeDecodeError:	data = json.load(open(LOC + old_filename, 'r', encoding='utf-8'))# Keep only relevant fieds, in the proper format for i in range(1,59385): # careful few files don't have metadata    data[str(i)]= {'author': data[str(i)]["author"], 'title': data[str(i)]['title'], 'language': data[str(i)]['language'], 'theme': data[str(i)]["subject"]}# Save in a new json file json.dump(data, open(LOC + new_filename, 'w', encoding='utf-8'), ensure_ascii=False, indent=1)